        //     email: req.body.email,
        //     level: req.body.level,
        //     username: req.body.username,
        //     password: req.body.password,
        // }
    )
    const saltrounds = 10
    staf.password = await bcrypt.hash(staf.password, saltrounds)
    await staf.save((error, result) => {
        if (error) {
            res.send(error)
        }
        else {
            res.json({data:staf})
        }
    });
}
