import mongoose from "mongoose";
import autopopulate from "mongoose-autopopulate"
import  Jwt  from "jsonwebtoken"
// import { boolean } from "joi";

const staffSchema = mongoose.Schema({
    _id:{
        type:Number,
    },
    name:{
        type:String,

    }
    ,qualification:{
        type:String,
    }
    ,gender:{
        type:String,
    }
    ,email:{
        type:String,
        
    }
    ,mobile:{
        type:String,
    }
    ,username:{
        type:String,
    }
    ,password:{
        type:String, 
    }
    ,isStaff:{
        type: Boolean,
    }

}, {
    timestamps: true
    , versionKey: false
})

