import mongoose from "mongoose";
import autopopulate from "mongoose-autopopulate"
import  Jwt  from "jsonwebtoken"

const studentSchema=mongoose.Schema({
    _id:{
        type:Number,
    },
    fullname:{
        type:String,

    },
    countryofnationality:{
        type: String,
        
    },
    religion:{
        type: String,
        
    },
    dateofbirth:{
        type: String,
        
    },
    placeofbirth:{
        type: String,
        
    },
    nationalid:{
        type: Number,
        
    },
    guardianname:{
        type: String,
        
    },
    jop:{
        type: String,
        
    },
    city:{
        type:String,
        
    },
    address:{
        type: String,
        
    },
    homephone:{
        type: Number,
        
    },
    mobile:{
        type: String,
        
    },
    school:{
        type:String,
        
    },
    fullyqualified:{
        type:String,
        
    },
    graduationyear:{
        type:Number,
        
    },
    roleofqualification:{
        type: String,
        
    },
    totalscore:{
        type:Number,
    },
    department:{
        type:String,
        
    },
    gender:{
        type:String,
        
    },
    email:{
        type:String,
        
    },
    level:{
        type:Number,
        
    },
    academicemail:{
        type:String,
        
    },
    password:{
        type:String,
        
    },
    isStudent: {
        type: Boolean,
        
        
    },
    gpa:{
        type:Number,
        
    },
    hoursused:{
        type: Number
    },
    remaininghourse:{
        type: Number,
        default : 135
    },
    achievedhours:{
        type: Number,
        default: 0
    },
    availablecredit:{
        type: Number,
        default : 15
    },
    currentcourses:[
        {
            type:String,
            
            ref:"courses",
            autopopulate:{
                select:"name"
            }
            
        }
    ],
    finishedcourses:[
        {
            type:String,
            
            ref:"courses",
            autopopulate:{
                select:"name"
            }
        }
    ],
    coursesgrades:[
        {
            type: ObjectId,
            ref:"StudentCourseGrade",
            
        }
    ],
    military:{
        type: Boolean,
        default:false
    }
    
},{ timestamps: true
    , versionKey: false
})
studentSchema.methods.generateTokens = function () {
    const token = Jwt.sign({
        _id:this._id,
        name:this.name,
        department:this.department,
        gender:this.gender,
        email:this.email,
        username:this.username,
        isStudent: this.isStudent,
        currentcourses: this.currentcourses,
        finishedcourses: this.finishedcourses,


    },"privatekey")
    return token

    
}
