import mongoose from "mongoose";
import autopopulate from "mongoose-autopopulate"
// const Joi = require('Joi');
import  Jwt  from "jsonwebtoken"

const studentSchema=mongoose.Schema({
    _id:{
        type:Number,
        
        
        
    },
    full_name:{
        type:String,
        

    },
    country_of_nationality:{
        type: String,
        
    },
    religion:{
        type: String,
        
    },
    date_of_birth:{
        type: Number,
        
    },
    place_of_birth:{
        type: String,
        
    },
    national_id:{
        type: Number,
        
    },
    guardian_name:{
        type: String,
        
    },
    jop:{
        type: String,
        
    },
    city:{
        type:String,
        
    },
    address:{
        type: String,
        
    },
    home_phone:{
        type: Number,
        
    },
    mobile:{
        type: Number,
        
    },
    school:{
        type:String,
        
    },
    fully_qualified:{
        type:String,
        
    },
    graduation_year:{
        type:Number,
        
    },
    role_of_qualification:{
        type: String,
        
    },
    total_score:{
        type:Number,
    },
    department:{
        type:String,
        
    },
    gender:{
        type:String,
        
    },
    email:{
        type:String,
        
    },
    level:{
        type:Number,
        
    },
    academic_email:{
        type:String,
        
    },
    password:{
        type:String,
        
    },
    isStudent: {
        type: Boolean,
        
    },
    gpa:{
        type:Number,
    },
    hoursused:{
        type: Number
    },
    remaininghourse:{
        type: Number,
        default : 125
    },
    avilablecredit:{
        type: Number,
        default : 12
    },
    currentcourses:[
        {
            type:String,
            
            ref:"courses",
            autopopulate:{
                select:"_id"
            }
            
        }
    ],
    finishedcourses:[
        {
            type:String,
            
            ref:"courses",
            autopopulate:{
                select:"name"
            }
        }
    ],
    
},{ timestamps: true
    , versionKey: false
})
studentSchema.methods.generateTokens = function () {
    const token = Jwt.sign({
        _id:this._id,
        name:this.name,
        department:this.department,
        gender:this.gender,
        email:this.email,
        username:this.username,
        isStudent: this.isStudent,
        currentcourses: this.currentcourses,
        finishedcourses: this.finishedcourses,


    },"privatekey")
    return token

    
}
